INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie sellitem', 0, 'Syntaxe: .confrerie sellitem #itemId\r\nPermet d\'ajouter un objet au personnage séléctionné contre de l\'argent (dépend de l\'objet) et un objet (dépend de votre niveau de marchand).\r\nSi vous vous séléctionnez, affichera le prix qui vous sera ajouté (vous ne pouvez pas vous ajouter un objet).');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie sellitem itemlvl', 4, 'Syntaxe: .confrerie sellitem itemlvl #itemLvl\r\nModifie le niveau d\'objet max que le joueur séléctionné peut vendre (entier positif).');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie sellitem itemquality', 4, 'Syntaxe: .confrerie sellitem itemquality #itemQuality\r\nModifie la qualitée d\'objet max que peut vendre le joueur séléctionné (0 - 5).');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie sellitem itemreq', 4, 'Syntaxe: .confrerie sellitem itemreq #itemId\r\nModifie l\'objet requis et qui sera retiré à chaque utilisation de la commande .conf sellitem pour le joueur séléctionné.');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie sellitem flags', 4, 'Syntaxe: .confrerie sellitem flags #flags\r\nModifie le flag du joueur séléctionné :\r\n1 Peut vendre des armes\r\n2 Peut vendre des armures\r\n4 Peut vendre des bijoux\r\n8 Peut vendre montures et compagnons\r\n16 Peut vendre à sa guilde\r\nAjoutez les valeurs pour combinés les autorisations.\r\nEx : Vendeur d\'armes et de bijoux avec autorisation de vente à sa guilde : 1 + 4 + 16 = 21 : .confrerie sellitem flags 21');
