INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie morph on', 0, 'Syntaxe: .confrerie morph on\nPermet d\'adopter la forme de votre race spéciale.');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie morph off', 0, 'Syntaxe: .confrerie morph off\nPermet de reprendre votre forme humaine.');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie morph set', 3, 'Syntaxe: .confrerie morph set #morphId\nAttribue la race d\ID #morphId au joueur séléctionné. Si aucun joueur n\'est séléctionné, vous attribue la race.');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie morph get', 3, 'Syntaxe: .confrerie morph get [$joueur]\nPermet de voir la race spéciale attribué au joueur indiqué ou sinon du joueur séléctionné.');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie morph create', 4, 'Syntaxe: .confrerie morph create "$nameDesc"\nPermet de créer une nouvelle race spéciale avec comment nom/commentaire $naleDesc. Utilisez .confrerie morph edit pour modifier les options de la race.');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie morph edit', 4, 'Syntaxe: .confrerie morph edit #morphId "$champ" #valeur|"#valeur"\nVous permet d\'éditer les champs d\'une race spéciale.\n\n$champ peut être remplacé par : displayid_m, displayid_w, aura1, aura2, aura3, spell1, spell2, spell3. Dans ce cas #valeur sera un entier positif.\nPeut être remplacé également par : speed, scale. Dans ce cas, sera un chiffre à virgule, par exemple : 1.5 ou 5.98 ou 4.\nPeut être remplacé également par : comments. Dans ce cas, #valeur sera une chaine de caractère qui devra être entourée par des " ".');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie morph delete', 4, 'Syntaxe: .confrerie morph delete #morphId\nPermet de suppriemer la race spéciale d\'ID #mophId');
INSERT INTO `command` (`name`, `security`, `help`) VALUES ('confrerie morph list', 3, 'Syntaxe: .confrerie morph list #champRecherche\nAffichera les races contenant #champRecherche dans comments.');